mapper.select:

select 

from t_bs_ov_dtal
where num_userid = #{numUserid,jdbcType=CHAR}
  and num_acc_pct = #{numAccPct,jdbcType=CHAR}
  and date_post = #{datePost,jdbcType=CHAR}
  and num_jrn_post = #{numJrnPost,jdbcType=INTEGER}

mapper.delete:

delete from t_bs_ov_dtal
where num_userid = #{numUserid,jdbcType=CHAR}
  and num_acc_pct = #{numAccPct,jdbcType=CHAR}
  and date_post = #{datePost,jdbcType=CHAR}
  and num_jrn_post = #{numJrnPost,jdbcType=INTEGER}

mapper.update:

update t_bs_ov_dtal


    ind_msk_proc = #{indMskProc,jdbcType=CHAR},


    cod_tr_type = #{codTrType,jdbcType=CHAR},


    amt_tr = #{amtTr,jdbcType=NUMERIC},


    amt_post = #{amtPost,jdbcType=NUMERIC},


    bal_cr = #{balCr,jdbcType=NUMERIC},


    txt_mer_name = #{txtMerName,jdbcType=VARCHAR},


    num_jrn_weixin = #{numJrnWeixin,jdbcType=CHAR},


where num_userid = #{numUserid,jdbcType=CHAR}
  and num_acc_pct = #{numAccPct,jdbcType=CHAR}
  and date_post = #{datePost,jdbcType=CHAR}
  and num_jrn_post = #{numJrnPost,jdbcType=INTEGER}


update t_bs_ov_dtal
set ind_msk_proc = #{indMskProc,jdbcType=CHAR},
  cod_tr_type = #{codTrType,jdbcType=CHAR},
  amt_tr = #{amtTr,jdbcType=NUMERIC},
  amt_post = #{amtPost,jdbcType=NUMERIC},
  bal_cr = #{balCr,jdbcType=NUMERIC},
  txt_mer_name = #{txtMerName,jdbcType=VARCHAR},
  num_jrn_weixin = #{numJrnWeixin,jdbcType=CHAR}
where num_userid = #{numUserid,jdbcType=CHAR}
  and num_acc_pct = #{numAccPct,jdbcType=CHAR}
  and date_post = #{datePost,jdbcType=CHAR}
  and num_jrn_post = #{numJrnPost,jdbcType=INTEGER}
